mapper.select:
/* Adm_TestId.xml - getMemberInfo */ SELECT idx , mem_no memNo , mem_userid memId , mem_phone memPhone , mem_passwd memPasswd , mem_nick memNick , mem_sex memSex , mem_birth_year memBirthYear , mem_birth_month memBirthMonth , mem_birth_day memBirthDay , mem_slct memSlct , mem_adid memAdid , mem_state memState , mem_join_date memJoinDate , last_upd_date lastUpdDate FROM rd_data.tb_member_basic WHERE
mem_phone = #{userInfoValue}
mem_no = #{userInfoValue}
mem_userid = #{userInfoValue}
mem_nick = #{userInfoValue}
LIMIT 1

/* Adm_TestId.xml - getTestIdInfo */
SELECT *
FROM rd_admin.tb_admin_test_account
WHERE mem_no = #{mem_no}

mapper.insert:
/* Adm_TestId.xml - insertTestId */
INSERT INTO rd_admin.tb_admin_test_account(
    MEM_NO
    , EMP_NO
    , EMP_NAME
    , RELATION
) values(
      #{mem_no}
    , #{emp_no}
    , #{emp_name}
    , #{relation}
)

/* Adm_TestId.xml - insertTestId */
INSERT INTO rd_admin.tb_admin_test_account_history(
    MEM_NO
    , EMP_NO
    , EMP_NAME
    , RELATION
) values(
      #{mem_no}
    , #{emp_no}
    , #{emp_name}
    , #{relation}
)

mapper.select:
/* Adm_TestId.xml - getTestIdSummary */ SELECT EMP_NAME , COUNT(EMP_NAME) CNT FROM rd_admin.tb_admin_test_account test , rd_data.tb_member_basic member where test.mem_no = member.mem_no
AND test.is_admin = #{search_adminId}
GROUP BY EMP_NAME ORDER BY COUNT(EMP_NAME) DESC

mapper.delete:
/* Adm_TestId.xml - deleteTestId */
delete from rd_admin.tb_admin_test_account
where mem_no in

    #{memNo}

mapper.update:
/* Adm_TestId.xml - updateAdminId */
update rd_admin.tb_admin_test_account
set is_admin = #{is_admin}
where mem_no = #{mem_no}

/* Adm_TestId.xml - updateAdminBadge */
update rd_data.tb_member_profile
set admin_badge = #{is_admin}
where


            mem_no = #{mem_no}


            mem_no in

                #{memNo}

mapper.insert:
/* Adm_TestId.xml - insertMemberBadge */
INSERT INTO rd_data.tb_member_badge(
    mem_no
    , slct_type
    , badge_value
    , start_date
    , end_date
) values(
      #{mem_no}
    , #{slct_type}
    , #{badge_value}
    , now()
    , '9999-12-31 23:59:59'
)

mapper.delete:
/* Adm_TestId.xml - deleteMemberBadge */
delete from rd_data.tb_member_badge
where


        mem_no = #{mem_no}


        mem_no in

            #{memNo}



    and slct_type = #{slct_type}

mapper.select:
/* Adm_TestId.xml - selectAdminManager */
select mem_no
from rd_admin.tb_admin_test_account
where is_admin = #{is_admin}
