mapper.select:
select idx , menu_name , menu_url , depth , order_no , parent_idx , icon , is_pop , is_comming_soon , is_use , mobile_yn , reg_date , last_upd_date from rd_admin.tb_admin_menu where is_use >= 1
and depth = #{depth}
order by depth, order_no

mapper.update:

update rd_data.tbl_code_define
set `value` = #{value}
where `type` = #{type}
    and code = #{code}
