INSERT INTO rd_data.hp_msg(
    UMID
    , MSG_TYPE
    , SEND_PHONE
    , DEST_PHONE
    , SUBJECT
    , MSG_BODY
    , VXML_FILE
    , REQUEST_TIME
    , SEND_TIME
    , SEND_NAME
    , mem_no
    , CINFO
)
SELECT
    #{umId}
    , #{msgType}
    , #{sendPhoneNo}
    , mem.mem_phone
    , #{subject, jdbcType=VARCHAR}
    , #{msgBody}
    , #{vxmlFile}


            , #{send_time}
            , #{send_time}


            , NOW()
            , NOW()


    , #{send_name}
    , mem.mem_no
    , #{cinfo}
FROM rd_data.tb_member_basic mem
    , rd_admin.tb_admin_test_account test
WHERE mem.mem_no = test.mem_no
    AND mem.mem_phone IS NOT NULL
    AND mem.mem_phone REGEXP '^(01[016789])([0-9]{3,4})([0-9]{4})'

INSERT INTO rd_data.hp_msg(
    UMID
    , MSG_TYPE
    , SEND_PHONE
    , DEST_PHONE
    , SUBJECT
    , MSG_BODY
    , VXML_FILE
    , REQUEST_TIME
    , SEND_TIME
    , SEND_NAME
    , mem_no
    , CINFO
)
SELECT
    #{umId}
    , #{msgType}
    , #{sendPhoneNo}
    , mem_phone
    , #{subject, jdbcType=VARCHAR}
    , #{msgBody}
    , #{vxmlFile}


            , #{send_time}
            , #{send_time}


            , NOW()
            , NOW()


    , #{send_name}
    , mem_no
    , #{cinfo}
FROM rd_data.tb_member_basic
WHERE mem_phone IS NOT NULL
    AND mem_phone REGEXP '^(01[016789])([0-9]{3,4})([0-9]{4})'
