mapper.update:
update rd_data.tb_member_fanboard
set `status` = 2, opName = #{opName}
where idx = #{idx}

where.if:
(bas.mem_no like = #{txt_search}
or sto.writer_no  = #{txt_search}
OR bas.mem_userid  = #{txt_search}
or sto.mem_userid  = #{txt_search}
OR bas.mem_nick like CONCAT('%',#{txt_search},'%')
or sto.mem_nick like CONCAT('%', #{txt_search}, '%')
OR bas.mem_id  = #{txt_search}
OR bas.mem_phone like CONCAT('%',#{txt_search},'%'))

mapper.select:
/* Con_BoardAdm.xml - selectStoryList*/ select a.* from ( select sto.idx as storyIdx, sto.room_no as room_no, sto.status as status, bas.mem_no as dj_mem_no, bas.mem_nick as dj_mem_nick, bas.mem_userid as dj_mem_userid, bas.mem_sex as dj_mem_sex, bas.mem_birth_year as dj_birth_year, bro.title as title, bro.state as broState, sto.mem_nick as send_mem_nick, sto.mem_userid as send_mem_userid, sto.mem_sex as send_mem_sex, sto.writer_no as send_mem_no, (select `inner` from rd_data.tb_member_basic where mem_no = sto.writer_no) as send_inner, (select bas.mem_birth_year from rd_data.tb_broadcast_room_story sto inner join rd_data.tb_member_basic bas on sto.writer_no = bas.mem_no where bas.mem_no = send_mem_no group by bas.mem_no) as send_birth_year, sto.write_date as send_date, sto.contents as story_content, (select count(*) from rd_data.tb_broadcast_room_story where room_no = bro.room_no) as storyCnt, (select count(*) from rd_data.tb_broadcast_room_story where writer_no = sto.writer_no) as sendStoryCnt, @RNUM := @RNUM + 1 as rowNum FROM rd_data.tb_broadcast_room_story sto JOIN rd_data.tb_broadcast_room bro ON sto.room_no = bro.room_no JOIN rd_data.tb_member_basic bas ON bro.mem_no = bas.mem_no , (select @RNUM := 0) r
order by sto.idx desc
order by storyCnt desc
order by sendStoryCnt desc
order by sto.idx asc
) a where rowNum between #{searchStartNo, jdbcType=INTEGER} and #{searchEndNo, jdbcType=INTEGER}

mapper.sql:
select date_format(session.last_upd_date,'%Y.%m.%d %H:%i:%s') as last_upd_date , session.mem_no as mem_no , profile.image_profile as image_profile , profile.msg_profile as msg_profile , basic.mem_nick as mem_nick , basic.mem_sex as mem_sex , basic.mem_birth_year as mem_birth_year , basic.mem_userid as mem_userid , basic.mem_join_date as mem_join_date , basic.mem_phone as mem_phone , basic.inner , (SELECT count(*) FROM rd_data.tb_member_profile_edit_history where mem_no=session.mem_no and edit_contents like '프로필메세지 변경%') as editCnt FROM rd_data.tb_member_basic basic INNER JOIN rd_data.tb_member_profile profile ON basic.mem_no = profile.mem_no JOIN rd_data.tb_member_connect_state `session` ON basic.mem_no = session.mem_no where session.mem_no is not null and profile.msg_profile is not null and profile.msg_profile != ''
and (basic.mem_no = #{searchText} OR basic.mem_userid = #{searchText} OR basic.mem_nick like CONCAT('%',#{searchText},'%') OR basic.mem_id = #{searchText} OR basic.mem_phone like CONCAT('%',#{searchText},'%'))
and date_format(profile.last_upd_date,'%Y.%m.%d') >= #{sDate}
and date_format(profile.last_upd_date,'%Y.%m.%d') <= #{eDate}
order by session.last_upd_date desc

