mapper.update:
/* Con_BoardAdm.xml - callProfileMsgDelete */
update rd_data.tb_member_profile
set msg_profile = ''
    , last_upd_date = now()
where mem_no=#{mem_no}

mapper.sql:
select a.title , a.cast_no , b.cast_mem_no , castMem.mem_nick as cast_mem_nick , castMem.mem_sex as cast_mem_sex , castMem.mem_birth_year as cast_birth_year , b.writer_mem_no , writerMem.mem_nick as writer_mem_nick , writerMem.mem_sex as writer_mem_sex , writerMem.mem_birth_year as writer_birth_year , writerMem.inner as writer_inner , b.contents , b.write_date , b.last_upd_date , `status` , b.idx as castReplyIdx from rd_data.tb_cast_room a join rd_data.tb_cast_room_board b on a.cast_no = b.cast_no left join rd_data.tb_member_basic castMem on b.cast_mem_no = castMem.mem_no left join rd_data.tb_member_basic writerMem on b.writer_mem_no = writerMem.mem_no inner join (select @RNUM := #{pageStart}) r where ((castMem.mem_no = #{searchText} OR castMem.mem_userid = #{searchText} OR castMem.mem_nick like CONCAT('%',#{searchText},'%') OR castMem.mem_id = #{searchText} OR castMem.mem_phone like CONCAT('%',#{searchText},'%')) or (writerMem.mem_no = #{searchText} OR writerMem.mem_userid = #{searchText} OR writerMem.mem_nick like CONCAT('%',#{searchText},'%') OR writerMem.mem_id = #{searchText} OR writerMem.mem_phone like CONCAT('%',#{searchText},'%')) ) and b.write_date BETWEEN concat(#{sDate},' 00:00:00') AND concat(#{eDate},' 23:59:59')
and `status` = #{status}
order by b.write_date desc

