mapper.select:
/* Con_BoardAdm.xml - selectStoryList*/ select * from ( select a.*, @RNUM := @RNUM + 1 as rowNum from ( select sto.idx as storyIdx, sto.room_no as room_no, sto.status as status, bas.mem_no as dj_mem_no, bas.mem_nick as dj_mem_nick, bas.mem_userid as dj_mem_userid, bas.mem_sex as dj_mem_sex, bas.mem_birth_year as dj_birth_year, bro.title as title, bro.state as broState, sto.mem_nick as send_mem_nick, sto.mem_userid as send_mem_userid, sto.mem_sex as send_mem_sex, sto.writer_no as send_mem_no, stobas.`inner` AS send_inner, stobas.mem_birth_year AS send_birth_year, sto.write_date as send_date, sto.contents as story_content, (select count(*) from rd_data.tb_broadcast_room_story where room_no = bro.room_no) as storyCnt, (select count(*) from rd_data.tb_broadcast_room_story where writer_no = sto.writer_no) as sendStoryCnt FROM rd_data.tb_broadcast_room_story sto JOIN rd_data.tb_broadcast_room bro ON sto.room_no = bro.room_no JOIN rd_data.tb_member_basic bas ON bro.mem_no = bas.mem_no JOIN rd_data.tb_member_basic stobas ON sto.writer_no = stobas.mem_no
union select sto.idx as storyIdx, sto.room_no as room_no, sto.status as status, bas.mem_no as dj_mem_no, bas.mem_nick as dj_mem_nick, bas.mem_userid as dj_mem_userid, bas.mem_sex as dj_mem_sex, bas.mem_birth_year as dj_birth_year, bro.title as title, bro.state as broState, sto.mem_nick as send_mem_nick, sto.mem_userid as send_mem_userid, sto.mem_sex as send_mem_sex, sto.writer_no as send_mem_no, stobas.`inner` AS send_inner, stobas.mem_birth_year AS send_birth_year, sto.write_date as send_date, sto.contents as story_content, (select count(*) from rd_data.tb_broadcast_room_story where room_no = bro.room_no) as storyCnt, (select count(*) from rd_data.tb_broadcast_room_story where writer_no = sto.writer_no) as sendStoryCnt FROM rd_data.tb_broadcast_room_story sto JOIN rd_data.tb_broadcast_room bro ON sto.room_no = bro.room_no JOIN rd_data.tb_member_basic bas ON bro.mem_no = bas.mem_no JOIN rd_data.tb_member_basic stobas ON sto.writer_no = stobas.mem_no
) a , (select @RNUM := 0) r
order by a.storyIdx desc
order by a.storyCnt desc
order by a.sendStoryCnt desc
order by a.storyIdx asc
) a1 where rowNum between #{searchStartNo, jdbcType=INTEGER} and #{searchEndNo, jdbcType=INTEGER}

/* Con_BoardAdm.xml - selectStoryListCnt*/ select sum(cnt) from (select count(*) AS cnt FROM rd_data.tb_broadcast_room_story sto JOIN rd_data.tb_broadcast_room bro ON sto.room_no = bro.room_no JOIN rd_data.tb_member_basic bas ON bro.mem_no = bas.mem_no JOIN rd_data.tb_member_basic stobas ON sto.writer_no = stobas.mem_no
union select count(*) AS cnt FROM rd_data.tb_broadcast_room_story sto JOIN rd_data.tb_broadcast_room bro ON sto.room_no = bro.room_no JOIN rd_data.tb_member_basic bas ON bro.mem_no = bas.mem_no JOIN rd_data.tb_member_basic stobas ON sto.writer_no = stobas.mem_no
) a

/* Con_BoardAdm.xml - selectStoryListSummary*/ select sum(a.totalCnt) AS totalCnt, sum(a.maleCnt) AS maleCnt, sum(a.femaleCnt) femaleCnt, sum(a.noneCnt) AS noneCnt from ( SELECT count(*) as totalCnt ,sum(if(bas.mem_sex = 'm',1,0)) as maleCnt ,sum(if(bas.mem_sex = 'f',1,0)) as femaleCnt ,sum(if(bas.mem_sex = 'n',1,0)) as noneCnt FROM rd_data.tb_broadcast_room_story sto JOIN rd_data.tb_broadcast_room bro ON sto.room_no = bro.room_no JOIN rd_data.tb_member_basic bas ON bro.mem_no = bas.mem_no JOIN rd_data.tb_member_basic stobas ON sto.writer_no = stobas.mem_no
union SELECT count(*) as totalCnt ,sum(if(bas.mem_sex = 'm',1,0)) as maleCnt ,sum(if(bas.mem_sex = 'f',1,0)) as femaleCnt ,sum(if(bas.mem_sex = 'n',1,0)) as noneCnt FROM rd_data.tb_broadcast_room_story sto JOIN rd_data.tb_broadcast_room bro ON sto.room_no = bro.room_no JOIN rd_data.tb_member_basic bas ON bro.mem_no = bas.mem_no JOIN rd_data.tb_member_basic stobas ON sto.writer_no = stobas.mem_no
) a

mapper.sql:
select date_format(session.last_upd_date,'%Y.%m.%d %H:%i:%s') as last_upd_date , session.mem_no as mem_no , profile.image_profile as image_profile , profile.msg_profile as msg_profile , basic.mem_nick as mem_nick , basic.mem_sex as mem_sex , basic.mem_birth_year as mem_birth_year , basic.mem_userid as mem_userid , basic.mem_join_date as mem_join_date , basic.mem_phone as mem_phone , basic.inner , (SELECT count(*) FROM rd_data.tb_member_profile_edit_history where mem_no=session.mem_no and edit_contents like '프로필메세지 변경%') as editCnt FROM rd_data.tb_member_basic basic INNER JOIN rd_data.tb_member_profile profile ON basic.mem_no = profile.mem_no JOIN rd_data.tb_member_connect_state `session` ON basic.mem_no = session.mem_no where session.mem_no is not null and profile.msg_profile is not null and profile.msg_profile != ''
and basic.mem_no = #{searchText}
and basic.mem_nick like concat('%', #{searchText}, '%')
and basic.mem_name = #{searchText}
and basic.mem_phone = #{searchText}
and basic.mem_userid = #{searchText}
and basic.mem_no in (SELECT distinct mem_no FROM rd_data.tb_member_session_history WHERE ip = #{searchText})
and basic.mem_no in (SELECT distinct mem_no FROM rd_data.tb_member_session_history WHERE device_uuid = #{searchText})
and (basic.mem_no = #{searchText} or basic.mem_nick like concat('%', #{searchText}, '%'))
and profile.last_upd_date between concat(#{sDate}, ' 00:00:00') and concat(#{eDate}, ' 23:59:59')
order by session.last_upd_date desc

