/* Con_BoardAdm.xml - editList */ select * from (
SELECT @RNUM := @RNUM +1 as rowNum ,idx ,room_no ,edit_contents as editContents ,op_name as opName ,last_upd_date as editDate FROM rd_data.tb_broadcast_room_edit_history inner join (select @RNUM := #{pageStart}) r where room_no=#{room_no} and edit_contents like '방송방공지변경%'
SELECT @RNUM := @RNUM +1 as rowNum ,idx ,mem_no ,edit_contents as editContents ,op_name as opName ,last_upd_date as editDate FROM rd_data.tb_member_profile_edit_history inner join (select @RNUM := #{pageStart}) r where mem_no=#{mem_no} and edit_contents like '프로필메세지 변경%'
) list limit #{pageStart}, #{pageCnt}

/* Con_BoardAdm.xml - tabCount */ select aa.totalCnt AS storyListCnt ,bb.totalCnt AS fanBoardListCnt ,cc.totalCnt AS fanBoardReplyCnt ,dd.totalCnt AS noticeListCnt ,ee.totalCnt AS broadNoticeListCnt ,ff.totalCnt AS profileMsgListCnt ,gg.totalCnt AS clipReplyCnt ,hh.totalCnt AS broadNoticeReplyCnt ,ii.totalCnt AS mailboxCnt from ( select '1' type , count(*) AS totalCnt from rd_data.tb_broadcast_room bro inner join rd_data.tb_broadcast_room_story sto on sto.room_no = bro.room_no INNER JOIN rd_data.tb_member_basic bas ON bro.mem_no = bas.mem_no
and sto.write_date >= DATE_FORMAT(#{start_sel}, '%Y.%m.%d 00:00:00')
and sto.write_date <= DATE_FORMAT(#{end_sel}, '%Y.%m.%d 23:59:59')
and bro.state != 4
and bro.state = 4
) aa, ( select '2' type , count(*) AS totalCnt from rd_data.tb_member_fanboard fan
and fan.last_upd_date >= DATE_FORMAT(#{start_sel}, '%Y.%m.%d 00:00:00')
and fan.last_upd_date <= DATE_FORMAT(#{end_sel}, '%Y.%m.%d 23:59:59')
and fan.depth = 1
) bb, (select '3' type , count(*) AS totalCnt from rd_data.tb_member_fanboard as fan
and fan.last_upd_date >= DATE_FORMAT(#{start_sel}, '%Y.%m.%d 00:00:00')
and fan.last_upd_date <= DATE_FORMAT(#{end_sel}, '%Y.%m.%d 23:59:59')
and fan.depth = 2
) cc, (select '4' type , count(*) AS totalCnt From rd_data.tb_broadcast_room a where CHAR_LENGTH(a.notice) > 0
and a.last_upd_date >= DATE_FORMAT(#{start_sel}, '%Y.%m.%d 00:00:00') and a.last_upd_date <= DATE_FORMAT(#{end_sel}, '%Y.%m.%d 23:59:59')
) dd, (select '5' type , count(*) AS totalCnt From rd_data.tb_member_broadcast_notice a
and a.last_upd_date >= DATE_FORMAT(#{start_sel}, '%Y.%m.%d 00:00:00') and a.last_upd_date <= DATE_FORMAT(#{end_sel}, '%Y.%m.%d 23:59:59')
) ee, ( SELECT '6' TYPE ,COUNT(*) AS totalCnt FROM rd_data.tb_member_basic basic INNER JOIN rd_data.tb_member_profile profile ON basic.mem_no = profile.mem_no JOIN rd_data.tb_member_connect_state `session` ON basic.mem_no = session.mem_no WHERE profile.msg_profile IS NOT NULL AND session.mem_no IS NOT NULL AND profile.msg_profile != '' AND profile.last_upd_date BETWEEN concat(#{start_sel},' 00:00:00') AND concat(#{end_sel},' 23:59:59')
) ff, (select '7' type , count(*) AS totalCnt from rd_data.tb_cast_room a join rd_data.tb_cast_room_board b on a.cast_no = b.cast_no inner join (select @RNUM := 0) r
and date_format(b.write_date,'%Y.%m.%d') >= #{start_sel}
and date_format(b.write_date,'%Y.%m.%d') <= #{end_sel}
) gg, (select '8' type, count(*) AS totalCnt from rd_data.tb_member_broadcast_notice_reply rep
and rep.last_upd_date >= DATE_FORMAT(#{start_sel}, '%Y.%m.%d 00:00:00')
and rep.last_upd_date <= DATE_FORMAT(#{end_sel}, '%Y.%m.%d 23:59:59')
) hh, (select '9' type, count(*) AS totalCnt from ( SELECT a.chat_no FROM rd_data.tb_mailbox_chatroom a INNER JOIN rd_data.tb_mailbox_chatroom_member b ON a.chat_no = b.chat_no AND a.mem_no = b.mem_no INNER JOIN ( SELECT chat_no, mem_no, `type`, msg, data1, data2, data3, data4, data5, data6, delete_yn, read_yn, last_upd_date FROM rd_data.tb_mailbox_chatroom_msg WHERE idx IN (SELECT MAX(idx) FROM rd_data.tb_mailbox_chatroom_msg GROUP BY chat_no) ) c ON a.chat_no = c.chat_no AND (b.mem_no = c.mem_no OR b.target_mem_no = c.mem_no)
and c.last_upd_date >= DATE_FORMAT(#{start_sel}, '%Y.%m.%d 00:00:00')
and c.last_upd_date <= DATE_FORMAT(#{end_sel}, '%Y.%m.%d 23:59:59')
) a ) ii
