mapper.select:
select * from rd_data.tb_member_basic where mem_no=#{mem_no}
union
select * FROM rd_data.tb_member_withdrawal_bak WHERE mem_no=#{mem_no}
limit 1

/* M_Member.xml - callMemberTestId */
select `inner` from rd_data.tb_member_basic where mem_no =#{mem_no}
union
select `inner` from rd_data.tb_member_withdrawal_bak where mem_no =#{mem_no}

/* M_Member.xml - callMemberBlock */
select block_day,block_end_date, block_type From rd_data.tb_member_basic where mem_no=#{mem_no}
 union
select block_day,block_end_date, block_type From rd_data.tb_member_withdrawal_bak where mem_no=#{mem_no}

SELECT COUNT(*) FROM rd_data.tb_broadcast_room_edit_history WHERE room_no IN ( SELECT room_no FROM rd_data.tb_broadcast_room WHERE mem_no = #{mem_no})
AND edit_contents LIKE concat('%',#{searchText},'%')
AND edit_contents NOT REGEXP #{searchText}

SELECT COUNT(*) FROM rd_data.tb_cast_room_edit_history WHERE cast_no IN ( SELECT cast_no FROM rd_data.tb_cast_room WHERE mem_no = #{mem_no})
AND edit_contents LIKE concat('%',#{searchText},'%')
AND edit_contents NOT REGEXP #{searchText}

/* M_Member.xml - callMemberPointHistory */
select @rownum := @rownum + 1 as rowNum, a.*
  FROM (SELECT last_upd_date AS editDate,
               edit_contents AS editContents,
               op_name       AS opName
          FROM rd_data.tb_member_profile_edit_history
         where `type` = 1 and mem_no=#{mem_no}
       ) a , (select @rownum := #{pageNo} ) AS b
 ORDER BY a.editDate DESC
 limit #{pageNo}, #{pageCnt}

mapper.update:
/* M_Member.xml - callMemberStateEditor */

    SELECT mem_state FROM rd_data.tb_member_basic WHERE mem_no=#{mem_no}

update rd_data.tb_member_basic set mem_state=1, block_day=0, block_end_date=null where mem_no=#{mem_no}

mapper.insert:
/* M_Member.xml - callMemberReport */
SELECT LAST_INSERT_ID()
insert into rd_data.tb_member_report (mem_no,mem_userid,mem_nick,mem_phone,mem_level,mem_grade,reported_mem_no,reported_userid, reported_nick,reported_phone,reported_level,reported_grade,room_no,reason,etc,status,op_code, op_msg,platform,browser,ip,op_name,op_date,reg_date) value (#{mem_no},#{mem_userid},#{mem_nick},'',1,'',#{reported_mem_no},#{reported_userid}, #{reported_nick},#{reported_phone},#{reported_level},#{reported_grade},null,#{slctReason},null,'2',#{slctType}, #{notiContents},'PC',#{browser},#{ip},#{opName},now(),now())

mapper.select:
/* M_Member.xml - callMemberEditor */
/* 방송중 상태 count */
select count(*) as cnt from rd_data.tb_broadcast_room where mem_no = #{mem_no} and state != 4

/* M_Member.xml - callMemberListening_check */
/* 청취중 상태 count */
select count(*) from rd_data.tb_broadcast_room_member where mem_no=#{mem_no} and state=0

mapper.update:
/* M_Member.xml - callMemberBasicReport_Edit */ update rd_data.tb_member_basic
set mem_state = #{slctType}
set mem_state = 3, block_type = 0, block_day = #{blockDay}, block_end_date = DATE_ADD( now(), interval #{blockDay} day)
set mem_state = 5 , block_type = 0
set mem_state = 4 , block_type = 0
set mem_state = 5 , block_type = 1
where mem_no = #{reported_mem_no}

mapper.insert:
/* M_Member.xml - callMemberNotification_Add */
insert into rd_data.tb_member_notification( mem_no, type_noti, contents, memo, target_mem_no, room_no, link  )
values ( #{reported_mem_no}
      , IFNULL(#{type_noti},70)
      , (select REPLACE(REPLACE(#{notiContents}
        , '%%mem_name%%',(select ifnull(mem_name,mem_nick) from rd_data.tb_member_basic where mem_no=#{reported_mem_no}))
        , '%%mem_nick%%',(select mem_nick from rd_data.tb_member_basic where mem_no=#{reported_mem_no})))
      , (select REPLACE(REPLACE(#{notimemo}
        , '%%mem_name%%',(select ifnull(mem_name,mem_nick) from rd_data.tb_member_basic where mem_no=#{reported_mem_no}))
        , '%%mem_nick%%',(select mem_nick from rd_data.tb_member_basic where mem_no=#{reported_mem_no})))
      , IFNULL(#{targetMemNo}, #{reported_mem_no})
      , IFNULL(#{targetRoomNo}, IFNULL(#{targetBoardIdx}, NULL))
      , IFNULL(#{targetRedirectUrl}, NULL)
)

insert into rd_data.tb_member_notification ( mem_no, type_noti, contents, memo, target_mem_no, room_no, link ) SELECT a.mem_no , IFNULL(#{type_noti},70) , (select REPLACE(REPLACE(#{notiContents} , '%%mem_name%%',(select ifnull(mem_name,mem_nick) from rd_data.tb_member_basic where mem_no=a.mem_no)) , '%%mem_nick%%',(select mem_nick from rd_data.tb_member_basic where mem_no=a.mem_no))) , (select REPLACE(REPLACE(#{notimemo} , '%%mem_name%%',(select ifnull(mem_name,mem_nick) from rd_data.tb_member_basic where mem_no=a.mem_no)) , '%%mem_nick%%',(select mem_nick from rd_data.tb_member_basic where mem_no=a.mem_no))) , IFNULL(#{targetMemNo}, #{reported_mem_no}) , IFNULL(#{targetRoomNo}, IFNULL(#{targetBoardIdx}, NULL)) , IFNULL(#{targetRedirectUrl}, NULL) FROM ( SELECT a.mem_no, b.all_ok, b.set_1, b.set_2, b.set_3, b.set_4, b.set_5, b.set_6, b.set_7 FROM rd_data.tb_member_stmp_token AS a INNER JOIN rd_data.tb_member_setting AS b ON a.mem_no = b.mem_no INNER JOIN rd_data.tb_member_basic AS c ON a.mem_no = c.mem_no WHERE length(a.device_token) > 0
AND a.os_type = #{slctOs}
) AS a

mapper.update:
/* M_Member.xml - callMemberBroadCastHide */
update rd_data.tb_broadcast_room
   set hide = #{hide}
 where mem_no = #{mem_no}
   and state !=4

       and room_no = #{room_no}

mapper.select:
