mapper.select:
/* Men_Rank.xml - getDjLiveCheck */
select count(*) as liveCnt, max(upd_date) as liveDate

    from rd_data.tb_ranking_dj_live_data_day where date_format(upd_date,'%Y.%m.%d')  =  #{sDate}


    from rd_data.tb_ranking_dj_live_data_week where date_format(upd_date,'%Y.%m.%d') >= #{sDate} and date_format(upd_date,'%Y.%m.%d')  <=  #{eDate}


    from rd_data.tb_ranking_dj_live_data_month where date_format(upd_date,'%Y.%m.%d')  >=  #{sDate} and date_format(upd_date,'%Y.%m.%d')  <=  #{eDate}


    from rd_data.tb_ranking_dj_live_data_year where date_format(upd_date,'%Y.%m.%d')  >=  #{sDate} and date_format(upd_date,'%Y.%m.%d')  <=  #{eDate}


    from rd_data.tb_ranking_time_dj_live_data


        where upd_date  >=  concat(#{sDate}, ' 00:00:00') and upd_date  <  concat(#{sDate}, ' 10:00:00')


        where upd_date  >=  concat(#{sDate}, ' 10:00:00') and upd_date  <  concat(#{sDate}, ' 19:00:00')


        where upd_date  >=  concat(#{sDate}, ' 19:00:00') and upd_date  <  concat(#{sDate}, ' 23:59:59')

/* Men_Rank.xml - getFanLiveCheck */
select count(*) as liveCnt, max(upd_date) as liveDate

    from rd_data.tb_ranking_fan_live_data_day where date_format(upd_date,'%Y.%m.%d')  =  #{sDate}


    from rd_data.tb_ranking_fan_live_data_week where date_format(upd_date,'%Y.%m.%d') >= #{sDate} and date_format(upd_date,'%Y.%m.%d')  <=  #{eDate}


    from rd_data.tb_ranking_fan_live_data_month where date_format(upd_date,'%Y.%m.%d')  >=  #{sDate} and date_format(upd_date,'%Y.%m.%d')  <=  #{eDate}


    from rd_data.tb_ranking_fan_live_data_year where date_format(upd_date,'%Y.%m.%d')  >=  #{sDate} and date_format(upd_date,'%Y.%m.%d')  <=  #{eDate}


    from rd_data.tb_ranking_time_fan_live_data


        where upd_date  >=  concat(#{sDate}, ' 00:00:00') and upd_date  <  concat(#{sDate}, ' 10:00:00')


        where upd_date  >=  concat(#{sDate}, ' 10:00:00') and upd_date  <  concat(#{sDate}, ' 19:00:00')


        where upd_date  >=  concat(#{sDate}, ' 19:00:00') and upd_date  <  concat(#{sDate}, ' 23:59:59')

/* Men_Rank.xml - AddDjPointVo */
select count(*) as liveCnt, max(upd_date) as liveDate
  from rd_data.tb_ranking_dj_live_data_month
 where date_format(upd_date,'%Y.%m.%d')  >=  #{sDate}
   and date_format(upd_date,'%Y.%m.%d')  <=  #{eDate}
