/* Mon_Exchange.xml - selectSummaryInfo */ -- 미처리 건 수 select ifnull(sum(cnt), 0) cnt from ( select 1 cnt from rd_data.tb_member_exchange exchange left join rd_admin.tb_special_dj special on exchange.mem_no = special.mem_no
and special.select_year = #{search_year} and special.select_month = #{search_month}
and #{search_year} <= special.select_year and special.select_year <= #{end_year}
and #{search_month} <= special.select_month and special.select_month <= #{end_month}
