and DATE_FORMAT(exchange.reg_date , '%Y%m') = concat(#{search_year}, #{search_month})
and concat(#{search_year}, #{search_month}, #{search_day}) <= DATE_FORMAT(exchange.reg_date, '%Y%m%d') and DATE_FORMAT(exchange.reg_date, '%Y%m%d') <= concat(#{end_year}, #{end_month}, #{end_day})
and special.mem_no is null
and special.mem_no is not null
and basic.inner = 0
)a
