mapper.update:
/* Mon_Exchange.xml - updateLastReject */
update rd_data.tb_member_exchange
set last_reject = 0
where mem_no = (
    select mem_no
    from rd_data.tb_member_exchange
    where idx = #{idx}
) and state = 2

