mapper.select:
/* Mon_Exchange.xml - selectEnableCnt */
select count(*) as enableCnt , sum(gold) as totalGold, sum(specialCnt) as totalSpecialCnt
from(

) a

/* Mon_Exchange.xml - selectEnableList */
select *
from(
    select @RNUM := @RNUM + 1 rowNum
        , list.*
    from (

    ) list
) list
where list.rowNum between #{searchStartNo, jdbcType=INTEGER} and #{searchEndNo, jdbcType=INTEGER}

/* Mon_Exchange.xml - selectExchangeCash */ select ifnull(count(summary.mem_no), 0) enableCnt , ifnull(sum(summary.gold), 0) totalGold , exchangeCnt from( select member.mem_no , wallet.gold , (select count(mem_no) from rd_data.tb_member_exchange exchange where exchange.mem_no = member.mem_no and exchange.state = 1) exchangeCnt from rd_data.tb_member_basic member inner join rd_data.tb_member_wallet wallet on member.mem_no = wallet.mem_no and 570 <= wallet.gold inner join rd_data.tb_member_profile profile on member.mem_no = profile.mem_no where 570 <= wallet.gold
and member.inner = 0
and profile.specialdj_badge = #{isSpecial}
) summary where 1=1
and 0 < exchangeCnt

/*환전내역이 없는 사람*/ select count(gold) count , ifnull(sum(general), 0) general , ifnull(sum(special), 0) special from( select wallet.gold, ifnull(case when profile.specialdj_badge = 0 then wallet.gold end,0) general, ifnull(case when profile.specialdj_badge = 1 then wallet.gold end,0) special from rd_data.tb_member_basic basic inner join rd_data.tb_member_profile profile on basic.mem_no = profile.mem_no inner join rd_data.tb_member_wallet wallet on basic.mem_no = wallet.mem_no and 570 <= wallet.gold left outer join (select mem_no, max(op_date) lastOpDate from rd_data.tb_member_exchange where state = 1 group by mem_no) exchange on basic.mem_no = exchange.mem_no where lastOpDate is null
and basic.inner = 0
and lastOpDate is not null
) sum
union all
/*3개월 이상 환전하지 않은사람*/ select count(gold) count , ifnull(sum(general), 0) general , ifnull(sum(special), 0) special from( select wallet.gold, ifnull(case when profile.specialdj_badge = 0 then wallet.gold end,0) general, ifnull(case when profile.specialdj_badge = 1 then wallet.gold end,0) special from rd_data.tb_member_basic basic inner join rd_data.tb_member_profile profile on basic.mem_no = profile.mem_no inner join rd_data.tb_member_wallet wallet on basic.mem_no = wallet.mem_no and 570 <= wallet.gold left outer join (select mem_no, max(op_date) lastOpDate from rd_data.tb_member_exchange where state = 1 group by mem_no) exchange on basic.mem_no = exchange.mem_no where lastOpDate is not null and lastOpDate > DATE_SUB(now(), interval 3 month)
and basic.inner = 0
and lastOpDate is not null
) sum
union all
/*3개월 내 환전한 회원*/ select count(gold) count , ifnull(sum(general), 0) general , ifnull(sum(special), 0) special from( select wallet.gold, ifnull(case when profile.specialdj_badge = 0 then wallet.gold end,0) general, ifnull(case when profile.specialdj_badge = 1 then wallet.gold end,0) special from rd_data.tb_member_basic basic inner join rd_data.tb_member_profile profile on basic.mem_no = profile.mem_no inner join rd_data.tb_member_wallet wallet on basic.mem_no = wallet.mem_no and 570 <= wallet.gold left outer join (select mem_no, max(op_date) lastOpDate from rd_data.tb_member_exchange where state = 1 group by mem_no) exchange on basic.mem_no = exchange.mem_no where lastOpDate is not null and lastOpDate <= DATE_SUB(now(), interval 3 month)
and basic.inner = 0
and lastOpDate is not null
) sum
