mapper.sql:
select @RNUM := @RNUM + 1 rowNum , pay_ok_date , rcpt_nm , order_id , receipt_ok_number , pay_amt , IF(receipt_code='i', '소득공제용', '지출증빙용') receipt_code from rd_data.tb_payment_succ, (select @RNUM := 0 ) AS b where pay_yn='y' and pay_way='VA' and receipt_code != 'n' and pay_ok_date between concat(#{txt_startSel},' 00:00:00') AND concat(#{txt_endSel},' 23:59:59')
and rcpt_nm = #{searchText}
and receipt_ok_number = #{searchText}
and ( rcpt_nm like concat ('%', upper(#{searchText}), '%') or receipt_ok_number like concat ('%', upper(#{searchText}), '%') )
order by pay_dt_comein asc

