mapper.insert:
/* Pay_Pay.xml - payAdd */
INSERT INTO rd_data.tb_payment_succ(
    order_id
    , mem_no
    , pay_way
    , pay_dt_comein
    , pay_ok_date
    , pay_ok_time
    , pay_amt
    , item_amt
    , item_code
    , pay_code
    , pay_info
    , pay_ip
    , login_media
    , app_ver
    , bill_id
    , service_id
    , os
    , dal_cnt
    , dal_yn
) values (
    #{order_id}
    , #{mem_no}
    , #{pay_way}
    , #{pay_dt_comein}
    , #{pay_ok_date}
    , #{pay_ok_time}
    , (select item_price from rd_data.tbl_charge_item where item_code = #{item_code})
    , #{item_amt}
    , #{item_code}
    , (select item_name from rd_data.tbl_charge_item where item_code = #{item_code})
    , #{pay_info}
    , #{pay_ip}
    , #{login_media}
    , (select ifnull(app_version, '') from rd_data.tb_member_session where mem_no = #{mem_no} order by last_upd_date desc limit 1)
    , #{bill_id}
    , #{service_id}
    , #{os}
    , (select dal from rd_data.tbl_charge_item where item_code = #{item_code})
    , #{dal_yn}
)

mapper.update:
/* Pay_Pay.xml - payUpdate */
UPDATE rd_data.tb_payment_succ pay
SET pay_yn = #{pay_yn}
WHERE order_id = #{order_id}
