mapper.select:
/* Sta_Push.xml - callPushHistoryListCnt */ SELECT COUNT(*) FROM ( SELECT history.idx ,history.send_date ,history.target_mem ,IF(history.admin_idx = '0', history.mem_no, NULL) AS mem_no ,IF(history.admin_idx = '0', history.mem_nick, admin.op_name) AS mem_nick ,history.admin_idx ,history.ins_date ,history.last_upd_date FROM rd_stmp.smtp_push_history AS history LEFT JOIN rd_admin.tb_admin_push AS admin ON history.admin_idx = admin.idx
history.send_date BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d 00:00:00') AND DATE_FORMAT(#{endDate}, '%Y-%m-%d 23:59:59')
AND CONCAT('1',history.target_platform) = #{platform}
AND history.send_type = #{send_type}
and history.mem_no in (SELECT mem_no_star FROM rd_data.tb_member_recv_alert WHERE mem_no_star = history.mem_no AND alert_yn = 1)
AND history.push_slct = #{push_slct}
AND (history.send_title like concat ('%', #{searchText}, '%') OR history.send_cont like concat ('%', #{searchText}, '%') OR (history.mem_no like concat ('%', #{searchText}, '%') OR history.mem_nick like concat ('%', #{searchText}, '%')) OR ifnull(admin.op_name,'자동') like concat ('%', #{searchText}, '%'))
) AS a

/* Sta_Push.xml - callPushHistoryList */ SELECT @rownum := @rownum + 1 as rowNum, a.* FROM ( SELECT history.idx ,history.send_date ,history.target_mem ,history.target_platform ,history.push_slct ,history.push_type ,history.image_type ,history.send_type ,history.target_cnt ,history.send_title ,history.send_cont ,IF(history.admin_idx = '0', history.mem_no, NULL) AS mem_no ,IF(history.admin_idx = '0', history.mem_nick, admin.op_name) AS mem_nick ,history.send_etc ,history.send_cnt ,history.succ_cnt ,history.fail_cnt ,history.succ_rate ,history.click_cnt ,history.click_rate ,history.admin_idx ,history.ins_date ,history.last_upd_date ,ifnull(admin.op_name,'자동') AS opName ,(SELECT COUNT(*) FROM rd_data.tb_member_recv_alert WHERE mem_no_star = history.mem_no AND alert_yn = 1) AS memberNotice FROM rd_stmp.smtp_push_history AS history LEFT JOIN rd_admin.tb_admin_push AS admin ON history.admin_idx = admin.idx
history.send_date BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d 00:00:00') AND DATE_FORMAT(#{endDate}, '%Y-%m-%d 23:59:59')
AND CONCAT('1',history.target_platform) = #{platform}
AND history.send_type = #{send_type}
AND history.push_slct = #{push_slct}
AND admin.op_name is not null
AND admin.op_name is null
and history.mem_no in (SELECT mem_no_star FROM rd_data.tb_member_recv_alert WHERE mem_no_star = history.mem_no AND alert_yn = 1)
AND (history.send_title like concat ('%', #{searchText}, '%') OR history.send_cont like concat ('%', #{searchText}, '%') OR (history.mem_no like concat ('%', #{searchText}, '%') OR history.mem_nick like concat ('%', #{searchText}, '%')) OR ifnull(admin.op_name,'자동') like concat ('%', #{searchText}, '%'))
) AS a, (select @rownum := #{pageStart}) AS b
a.ins_date desc
LIMIT #{pageStart}, #{pageCnt}

SELECT 'summary' AS idx , SUM(a.target_cnt) AS target_cnt , SUM(a.send_cnt) AS send_cnt , SUM(a.succ_cnt) AS succ_cnt , SUM(a.fail_cnt) AS fail_cnt , IF( SUM(a.send_cnt) > 0, ROUND((SUM(a.succ_cnt)/SUM(a.send_cnt))*100, 2), 0) AS succ_rate , SUM(a.click_cnt) AS click_cnt , if( SUM(a.succ_cnt) > 0 , ROUND((SUM(a.click_cnt)/SUM(a.succ_cnt))*100, 2), 0) AS click_rate FROM( SELECT history.idx ,history.send_date ,history.target_mem ,history.target_platform ,history.push_slct ,history.push_type ,history.image_type ,history.send_type ,history.target_cnt ,history.send_title ,history.send_cont ,IF(history.admin_idx = '0', history.mem_no, NULL) AS mem_no ,IF(history.admin_idx = '0', history.mem_nick, admin.op_name) AS mem_nick ,history.send_etc ,history.send_cnt ,history.succ_cnt ,history.fail_cnt ,history.succ_rate ,history.click_cnt ,history.click_rate ,history.admin_idx ,history.ins_date ,history.last_upd_date FROM rd_stmp.smtp_push_history AS history LEFT JOIN rd_admin.tb_admin_push AS admin ON history.admin_idx = admin.idx
history.send_date BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d 00:00:00') AND DATE_FORMAT(#{endDate}, '%Y-%m-%d 23:59:59')
AND CONCAT('1',history.target_platform) = #{platform}
AND history.send_type = #{send_type}
and history.mem_no in (SELECT mem_no_star FROM rd_data.tb_member_recv_alert WHERE mem_no_star = history.mem_no AND alert_yn = 1)
AND history.push_slct = #{push_slct}
) AS a
AND a.send_title like concat ('%', #{searchText}, '%')
AND a.send_cont like concat ('%', #{searchText}, '%')
AND (a.mem_no like concat ('%', #{searchText}, '%') OR a.mem_nick like concat ('%', #{searchText}, '%'))
AND (a.send_title like concat ('%', #{searchText}, '%') OR a.send_cont like concat ('%', #{searchText}, '%') OR (a.mem_no like concat ('%', #{searchText}, '%') OR a.mem_nick like concat ('%', #{searchText}, '%')))
